import React, { useState, useEffect } from "react";
import { Row, Container, Col, InputGroup } from "react-bootstrap";
import Structure from "../../Component/Structure";
import DependenciasRurales from "./DependenciasRurales";
import CardFigure from "../../Component/CardFigureCreator";

export default function Creador(props) {
  const [valu, setValu] = useState("NONE");
  const [todos, setTodos] = useState();
  const getColumnas = async () => {
    try {
      const response = await fetch(
        "http://127.0.0.1:8000/matriculas/columnas/",
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            col: value,
          }),
        }
      );
      const json = await response.json();
      setTodos(json);

      return json;
    } catch (error) {
      console.error(error);
    }
  };
  const options = [
    { value: "RBD", label: "RBD" },
    { value: "yummy", label: "yummy" },
    { value: "red", label: "red" },
    { value: "green", label: "green" },
    { value: "yellow", label: "yellow" },
  ];

  return (
    <Structure>
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2 text-center">
            <div className="col-sm-12">
              <h1 className="m-0"> Creador de graficos </h1>
            </div>

            {/* /.col */}
          </div>
          {/* /.row */}
        </div>
        {/* /.container-fluid */}
      </div>
      <Container fluid>
        <Row>
          <Col lg={3}>
            <InputGroup>
              <label>Ingresa una variable</label>
              <select
                value={valu}
                onChange={(e) => setValu(e.target.value)}
                placeholder="NONE"
              >
                {options.map(({ value, label }, index) => (
                  <option value={value}>{label}</option>
                ))}
              </select>
            </InputGroup>
          </Col>
          <Col lg={9}>
            <CardFigure nombre={valu} icono={"fas fa-chart-bar mr-1"}>
              <DependenciasRurales value={valu}></DependenciasRurales>
            </CardFigure>
          </Col>
        </Row>
      </Container>
    </Structure>
  );
}
