import React , {useState, useEffect} from 'react';
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

export default function ChartDependencias() {
    const url ='https://analizador-backend.herokuapp.com/directorio/dependencia/'
    const [todos, setTodos]=useState()
    const fetchApi= async () =>{
      const response =await fetch(url)
      const responseJSON=await response.json()
      setTodos(responseJSON)
    }
  
    const labels = [];
    const scores = [];
    
    for (var a in todos){
      labels.push(todos[a].cod)
      scores.push(todos[a].total)
      
    }
    
    const data = {
      labels,
      datasets: [
        {
          label: "Mis datos",
          data: scores,
          backgroundColor: 'rgb(172,108,52)',
        },
      ],
    };
    useEffect(() => {
      fetchApi()
    }, [])
   
    return <Pie data={data} height={250} options={{maintainAspectRatio: false}}  />;
  }
