import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";

export default function Totales(props) {
  const { url_base } = props;

  const url = `${url_base}/total/`;

  const [query, setQuery] = useState();
  
  const fetchApi = async () => {
    const response = await fetch(url);
    const responseJSON = await response.json();
    setQuery(responseJSON);
  };

  const labels = [];
  const scores = [];

  for (var a in query) {
    scores.push(query[a].total);
  }

  const data = {
    datasets: [
      {
        label: "Mis datos",
        data: scores,
        backgroundColor: "rgba(210,177,144,0.9)",
      },
    ],
  };

  useEffect(() => {
    fetchApi();
  }, []);
  console.log(query);

  return (
    <Line data={data} height={250} options={{ maintainAspectRatio: false }} />
  );
}
