import React, { useState, useEffect } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Line, Bar } from "react-chartjs-2";
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

export default function Comunas(props) {
  const { url_base } = props;
  const url = `${url_base}/total/`;
  const [query, setQuery] = useState();

  const fetchApi = async () => {
    const response = await fetch(url, {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        variable: "NOM_COM_RBD",
      }),
    });
    const responseJSON = await response.json();
    setQuery(responseJSON);
  };
  useEffect(() => {
    fetchApi();
  }, []);

  var array = new Set();
  const scores = [];


  for (var a in query) {
    console.log(query[a]);
    const lista2 = []
    for (var b in query[a].RBD) {
      var nose = query[a].RBD[b]
      console.log(query[a].RBD[b]);
      lista2.append(nose)
      
      {/**  Añadiendo los nombres de las comunas al set **/}
      array.add(b);
    }
    scores.append(lista2)

  }
  console.log(scores);

  const colors = [
    "rgba(148,156,196,0.9)",
    "rgba(172,108,52,0.9)",
    "rgba(210,177,144,0.9)",
    "rgba(60,141,188,0.9)",
    "rgba(190,198,220,0.9)",
  ];
  const data = {
    labels: ["2018", "2019", "2020", "2021"],
    datasets: [
      {
        label: "Est. totales",
        data: scores,
        borderColor: colors[3],
        backgroundColor: colors[3],
        fill: true,
      },
    ],
  };

  return (
    <Line data={data} height={250} options={{ maintainAspectRatio: false }} />
  );
}
