import React, { useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

export default function DependenciasRurales(props) {
  const { cambio, url_base } = props;
  const labels = ["a", "b", "c"];
  const [todos, setTodos]=useState()

  const getComunas = async () => {
    try {
      const response = await fetch(
        "http://127.0.0.1:8000/matriculas/creador/prueba/",
        {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            col: "NOM_COM_RBD",
          }),
        }
      );
      const json = await response.json();
      console.log(json);
      setTodos(json);

      const labels = [];
      const scores = [];

      for (var a in todos) {
        labels.push(todos[a].cod);
        scores.push(todos[a].total);
      }
      return json;
    } catch (error) {
      console.error(error);
    }
  };
  useEffect(() => {
    getComunas();
  }, []);
  const data = {
    labels,
    datasets: [
      {
        label: "Mis datos",
        data: scores,
        backgroundColor: ['rgba(148,156,196,0.9)' , 'rgba(172,108,52,0.9)', 'rgba(210,177,144,0.9)', 'rgba(60,141,188,0.9)' ,'rgba(190,198,220,0.9)'],
      },
    ],
  };

  return (
    <Bar
      height={250}
      options={{ maintainAspectRatio: false }}
      data={{
        labels,
        datasets: [
          {
            label: "Est. urbanos",
            data: [1, 2, 3, 4],
            backgroundColor: "rgba(148,156,196,0.9)",
          },
        ],
      }}
    />
  );
}
