import React, { useState, useEffect } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Line } from "react-chartjs-2";
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

export default function Totales(props) {
  const { url_base } = props;

  const url = `${url_base}/total/`;

  const [query, setQuery] = useState();
  const [Estados, setEstados] = useState();


  const fetchApi = async () => {
    const response = await fetch(url);
    const responseJSON = await response.json();

    setQuery(responseJSON);
    console.log(query);
  };

  const labels = [];
  const scores = [];

  useEffect(() => {
    fetchApi();
  }, []);

  for (var a in query) {
    scores.push(query[a].total);
  }

  const data = {
    labels: ["2019", "2020", "2021"],
    datasets: [
      {
        label: "Establecimientos totales",
        data: scores,
        backgroundColor: "rgba(148,156,196,0.7)",
        borderColor: "rgba(148,156,196,1)",
        fill: true,
      },
    ],
  };

  return (
    <Line data={data} height={250} options={{ maintainAspectRatio: false }} />
  );
}
