import React, { useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

export default function Totales(props) {
  const { cambio, url_base } = props;
  const url = `${url_base}/totales/`;
  const [query, setquery] = useState();
  const fetchApi = async () => {
    const response = await fetch(url);
    const responseJSON = await response.json();
    setquery(responseJSON);
  };

  const labels = [];
  const scores = [];

  for (var a in query) {
    labels.push(query[a].cod);
    scores.push(query[a].total);
  }

  const data = {
    labels,
    datasets: [
      {
        label: "Mis datos",
        data: scores,
        backgroundColor: "rgba(210,177,144,0.9)",
      },
    ],
  };
  useEffect(() => {
    fetchApi();
  }, []);

  return (
    <Bar data={data} height={250} options={{ maintainAspectRatio: false }} />
  );
}
