import React , {useState, useEffect} from 'react';
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

export default function DependenciasRurales() {
    const url ='http://127.0.0.1:8000/directorio/dependencia-rural/'
    const [todos, setTodos]=useState()
    const fetchApi= async () =>{
      const response =await fetch(url)
      const responseJSON=await response.json()
      console.log(responseJSON)
      setTodos(responseJSON)
    }
  
    const codigos = [];
    const valor_rural = [];
    const valor_urbano = [];
    
    for (var a in todos){
      console.log(todos[a])
      console.log(todos[a].cod)

      if(!codigos.includes(todos[a].cod)){
        codigos.push(todos[a].cod)
      }
      if(todos[a].rural == 0){
        valor_urbano.push(todos[a].total)

      }



      
    }
    console.log(valor_urbano)
    
    const data = {
      codigos,
      datasets: [
        {
          label: "Mis datos",
          data: valor_rural,
          backgroundColor: 'rgb(17,18,52)',
        },
        {
            label: "Mis datos pero en rojo",
            data: valor_urbano,
            backgroundColor: '#ff0000',
          },
      ],
    };
    useEffect(() => {
      fetchApi()
    }, [])
   
    return <Bar data={data} options={{ responsive: true }} />;
  }
