import React, { useState, useEffect } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Line } from "react-chartjs-2";
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

export default function Totales(props) {
  const { url_base } = props;

  const url = `${url_base}/total/`;

  const [query, setQuery] = useState();
  const [estados, setEstados] = useState();

  const fetchApi = async () => {
    const response = await fetch(url);
    const responseJSON = await response.json();

    setQuery(responseJSON);
  };
  const fetchEstados = async () => {
    const response = await fetch(`${url_base}/estados/`);
    const responseJSON = await response.json();

    setEstados(responseJSON);
    console.log(responseJSON);
  };

  useEffect(() => {
    fetchApi();
    fetchEstados();
  }, []);

  const scores = [];
  const scores1 = [];
  const scores2 = [];
  const scores3 = [];

  for (var a in query) {
    scores.push(query[a].total);
    for (var b in estados[a]) {
      scores1.push(estados[a].rbd[a + 1]);
      scores2.push(estados[a].rbd[a + 1]);
      scores3.push(estados[a].rbd[a + 1]);
    }
  }

  const data = {
    labels: ["2019", "2020", "2021"],
    datasets: [
      {
        label: "Establecimientos totales",
        data: scores,
        backgroundColor: "rgba(148,156,196,0.7)",
        borderColor: "rgba(148,156,196,1)",
        fill: true,
      },
      {
        label: "1",
        data: scores,
        backgroundColor: "rgba(148,156,196,0.7)",
        borderColor: "rgba(148,156,196,1)",
        fill: true,
      },
      {
        label: "2",
        data: scores,
        backgroundColor: "rgba(148,156,196,0.7)",
        borderColor: "rgba(148,156,196,1)",
        fill: true,
      },
      {
        label: "3",
        data: scores,
        backgroundColor: "rgba(148,156,196,0.7)",
        borderColor: "rgba(148,156,196,1)",
        fill: true,
      },
    ],
  };

  return (
    <Line data={data} height={250} options={{ maintainAspectRatio: false }} />
  );
}
