import React , {useState, useEffect} from 'react';
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

export default function DependenciasRurales() {
    const url ='http://127.0.0.1:8000/directorio/dependencia-rural/'
    const [nada, setNada]=useState()
    const fetchApi= async () =>{
      const response =await fetch(url)
      const responseJSON=await response.json()
      console.log(responseJSON)
      setNada(responseJSON)
    }
  
    const codigos = [];
    const valor_rural = [];
    const valor_urbano = [];
    
    for (var a in nada){
      console.log(nada[a])
      console.log(nada[a].cod)

      if(!codigos.includes(nada[a].cod)){
        codigos.push(nada[a].cod)
      }
      if(nada[a].rural == 0){
        valor_urbano.push(nada[a].total)

      }
      
    }
    
    const data_dos = {
      codigos,
      datasets: [
        {
            label: "Mis datos pero en rojo",
            data: [1,2,3,4,5],
            backgroundColor: 'rgb(172,108,52)',
          },
      ],
    };console.log(codigos)
    useEffect(() => {
      fetchApi()
    }, [])
   
    return <Bar data={data_dos} />;
  }
